#!/bin/bash CDOCSRV=${1:-cdoc} NC='\033[0m' LG='\033[1;32m' RD='\033[1;31m' # CDoc Sites Checker echo -n "URL 1: " read url1 printf "\n" printf "Sites\n" printf "++++++++++++++++++\n" printf "${LG}$url1${NC}\n" printf "\n" printf "Ping Status\n" printf "++++++++++++++++++\n" sleep $((RANDOM%10)) if curl --output /dev/null --silent --head --fail $url1/sitemap.xml then wget -qO- "http://www.google.com/webmasters/tools/ping?sitemap="$url1"/sitemap.xml" &> /dev/null wget -qO- "http://www.bing.com/ping?siteMap="$url1"/sitemap.xml" &> /dev/null printf "${LG}$url1${NC}\n" else printf "${RD}$url1${NC}\n" fi 